import { useDispatch, useSelector } from 'react-redux'
import { anecdoteVote } from '../reducers/anecdoteReducer'
import { setNotificationWithTimeout } from '../reducers/notificationReducer' // Import the new action

const Anecdote = ({anecdote, handleClick}) => {
  return (
    <div>
      {anecdote.content}
      <div>
        has {anecdote.votes}
        <button onClick={handleClick}>vote</button>
      </div>
    </div>
  )
}

const AnecdoteList = () => {
    const dispatch = useDispatch()
    const anecdotes = useSelector(({filter, anecdotes}) => {
      if (filter === '') {
        return anecdotes
      }
    return anecdotes.filter(n => n.content.toLowerCase().indexOf(filter.toLowerCase()) >= 0)
    })

    const sortedAnecdotes = [...anecdotes].sort((a, b) => b.votes - a.votes)

    return (
        <div>
          {sortedAnecdotes.map(anecdote => 
            <Anecdote
              key={anecdote.id}
              anecdote={anecdote}
              handleClick={() => {
                dispatch(anecdoteVote(anecdote.id))
                dispatch(setNotificationWithTimeout(`you voted '${anecdote.content}'`, 10))
                /*dispatch(setNotification(`you voted '${anecdote.content}'`))*/
                }
              }
            />
          )}
    </div>
  )
}

export default AnecdoteList
